'use strict';

/* Fibonacci Number
 * Function: fibonacci()
 */

var validate = require('../validation/positive-integer');

function fib(num) {
	if (num <= 2) {
		return 1;
	}
	var n1 = 1,
	    n2 = 1,
	    total = void 0;
	for (var i = 3; i <= num; i++) {
		total = n1 + n2;
		n1 = n2;
		n2 = total;
	}
	return total;
}

exports.fibonacci = function (num) {
	validate(num, 'fibonacci');
	return fib(num);
};