'use strict';

/* Neon Number
 * Function: isNeon()
 */

var validate = require('../validation/positive-integer');

exports.isNeon = function (num) {
	validate(num, 'isNeon');
	return num == Math.pow(num, 2).toString().split('').reduce(function (prev, next) {
		return prev + parseInt(next, 10);
	}, 0);
};