'use strict';

/*
 * Pipe Validation
 */

module.exports = function (arg, func) {
	var flag = 0;
	if (arg && arg.constructor == Array) arg.forEach(function (item) {
		if (typeof item != 'function') flag = 1;
	});
	if (!arg.length || arg.constructor != Array || flag) throw new TypeError('Invalid argument received: ' + JSON.stringify(arg) + '\n\'' + func + '()\' only accept an array of functions!\n');
};